function search_parser(html) {
var jList = JSON.parse(html);
var list = [];
jList.forEach(function(item){
var bm = {};
bm.cid = item.id
bm.title = item.title
bm.cover = item.cover
bm.author = item.authors
bm.update = item.last_name
list.push(bm);
});
return JSON.stringify(list);
}
function dateFormat(longTypeDate){ 
 var dateTypeDate = ""; 
 var date = new Date(); 
 date.setTime(longTypeDate); 
 dateTypeDate += date.getFullYear(); //年 
 dateTypeDate += "-" + getMonth(date); //月 
 dateTypeDate += "-" + getDay(date); //日 
 return dateTypeDate;
} 
//返回 01-12 的月份值 
function getMonth(date){ 
 var month = ""; 
 month = date.getMonth() + 1; //getMonth()得到的月份是0-11 
 if(month<10){ 
  month = "0" + month; 
 } 
 return month; 
} 
//返回01-30的日期 
function getDay(date){ 
 var day = ""; 
 day = date.getDate(); 
 if(day<10){ 
  day = "0" + day; 
 } 
 return day; 
}
 function comic_parser(html) {
 var jobj = JSON.parse(html);
 var jList = jobj.chapters[0].data
 var json = [];
   var bm = {};
   bm.title = jobj.title;
   bm.cover = jobj.cover;
   bm.intro = jobj.description;
   bm.update = dateFormat(jobj.last_updatetime*1000)
   bm.author = jobj.authors[0].tag_name;
   bm.isPage = true
   json.push(bm)
   var list = [];
   jList.forEach(function(item){
     var c = {};
     c.title = item.chapter_title;
     c.chapterId = item.chapter_id;
     list.push(c)
   })
   var array = {};
   array.chapters = list
   json.push(array)
   return JSON.stringify(json)
 }
